//import { useState } from 'react'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'

const Blog = () => {
  const id = useParams().id
  console.log(id, 'is id in blog component')

  const blog = useSelector(state => state.blogs)
  console.log(blog, 'is blog in Blog component')

  const increaseLikes = async (id) => {
    const blog = blogs.find((diary) => diary.id === id)
    console.log(blog, 'is blog')
    //console.log(blog.id, 'is blog id')
    //console.log(blog.user, 'is blog user')
    //console.log(blog.user.id, 'is blog user id')
    console.log(blog.likes, 'is blog likes before update')

    const updatedBlogInfo = {
      likes: blog.likes+1,
      author: blog.author,
      title: blog.title,
      url: blog.url
    }

    console.log(updatedBlogInfo, 'is updated blog info')
    dispatch(addingVote(id, updatedBlogInfo))
    dispatch(setNotif(`You added a like to "${blog.title}"`, 5))
  }


  if (blog) {
    const displayBlog = blog.find((diary) => diary.id === id)
    console.log(displayBlog, 'is displayblog')

    if (displayBlog.user) {
      return (
        <div>
          <h1>{displayBlog.title}</h1>
          <p>{displayBlog.url}</p>
          <p>{displayBlog.likes} likes</p>
          <p>added by {displayBlog.user.personName}</p>
        </div>
      )
    }
    else {
      return (
        <div>
          <h1>{displayBlog.title}</h1>
          <p>{displayBlog.url}</p>
          <p>{displayBlog.likes} likes</p>
          <p>added by {displayBlog.user}</p>
        </div>
      )
    }
  }
  else {
    return (
      <p>loading...</p>
    )
  }

  //the user wants to see detailed view

}
export default Blog
