import {useState} from 'react'
import blogService from '../services/blogs'

const Blog = ({blog, userInfo, increaseLikes}) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  
  const [shown, setShown] = useState(false)
  const ident = blog.id
  //console.log(ident)
  
  //console.log(userInfo, 'is userinfo')
  
  const toggleDetail = () => {
    setShown(!(shown))
  }
  
  const deleteEntry = (id) => {
    const agree = window.confirm(`Are you sure you want to delete ${blog.title}?`)
    if (agree) {
      blogService
        .delete(id)
        .then(()=> console.log('we deleted'))
    }
  
  }

  const label = shown
    ? 'hide'
    : 'view'
  
  if (shown) {
    if (blog.user) {
    return (
      <div style={blogStyle}>
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
        <p>{blog.url}</p>
        <p>{blog.id}</p>
        <p>likes: {blog.likes} <button onClick={increaseLikes}>like</button></p>
        <p>{blog.user.personName}</p>
        <p>pee</p>
        <button>remove</button>
      </div>
      </div>
    )
   } else {
     return (
      <div style={blogStyle}>
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
        <p>{blog.url}</p>
        <p>{blog.id}</p>
        <p>likes: {blog.likes} <button onClick={increaseLikes}>like</button></p>
        <p>{userInfo.personName}</p>
        <button>remove</button>
      </div>
      </div>
     )
   } 
  } else {
    return (
      <div style={blogStyle}>
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
      </div>
      </div>
      
      )
  }  
}

export default Blog
