const Blog = ({blog, toggleDetail}) => {

  const shown = false

  const label = shown
    ? 'hide'
    : 'view'
  
  if (onShow) {
    return (
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
        <p>{blog.url}</p>
        <p>{blog.likes} <button>likes</button></p>
        <p>{blog.user}</p>
      </div>
    ) 
  
  } else {
    return (
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
      </div>
      
      )
  }  
}

export default Blog
