import { useState } from 'react'

const Blog = ({ blog, userInfo, increaseLikes, deleteEntry }) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [shown, setShown] = useState(false)

  const toggleDetail = () => {
    //console.log(showing, 'is showing')
    setShown((shown) => !shown)
  }

  //{userInfo.name === blog.author && <button onClick={deleteEntry}>delete</button>}

  const label = shown
    ? 'hide'
    : 'view'

   return (
   <div style={blogStyle} className="default-view">
     <p>{blog.title} by {blog.author} <button onClick={() => toggleDetail(!shown)}>{label}</button></p>

   </div>
   )
}
export default Blog
