import {useState} from 'react'
import blogService from '../services/blogs'

const Blog = ({blog, userInfo, increaseLikes}, blogLikes) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  
  const [shown, setShown] = useState(false)
  
  
  //console.log(userInfo, 'is userinfo')
  
  const toggleDetail = () => {
    setShown(!(shown))
  }
  
  const deleteEntry = (id) => {
    const agree = window.confirm(`Are you sure you want to delete ${blog.title}?`)
    if (agree) {
      blogService
        .delete(id)
        .then(()=> console.log('we deleted'))
    }
  }

  const label = shown
    ? 'hide'
    : 'view'
  
  //the user wants to see detailed view
  if (shown) {
    //the blog contains info on the user
    if (blog.user){
      //the user just updated the likes
      if (blogLikes) {
        return (
          <div style={blogStyle}>
            <div>
              <p>{blog.title} by {blog.author} <button onClick={toggleDetail}>{label}</button></p>
              <p>{blog.url}</p>
              <p>likes: {blogLikes} <button onClick={increaseLikes}>like</button></p>
              <p>{blog.user.personName}</p>
            </div>
          </div>       
        )
      }
      //the user did not just update the likes
      else {
        return (
         <div style={blogStyle}>
            <div>
              <p>{blog.title} by {blog.author} <button onClick={toggleDetail}>{label}</button></p>
              <p>{blog.url}</p>
              <p>likes: {blog.likes} <button onClick={increaseLikes}>like</button></p>
              <p>{blog.user.personName}</p>
            </div>
          </div> 
        )
      }
    }
    //the blog info must come from user token
    else {
      //the user just updated the likes
      if (blogLikes) {
        return (
         <div style={blogStyle}>
            <div>
              <p>{blog.title} by {blog.author} <button onClick={toggleDetail}>{label}</button></p>
              <p>{blog.url}</p>
              <p>likes: {blogLikes} <button onClick={increaseLikes}>like</button></p>
              <p>{userInfo.personName}</p>
            </div>
          </div> 
        )
      }
      //the user did not just update the likes
      else{
        return (
        <div style={blogStyle}>
         <div>
           <p>{blog.title} by {blog.author} <button onClick={toggleDetail}>{label}</button></p>
         </div>
      </div>
        
        )
      }
    }
  }
  //the user does not want to see detailed view
  else {
    return(
      <div style={blogStyle}>
         <div>
           <p>{blog.title} by {blog.author} <button onClick={toggleDetail}>{label}</button></p>
         </div>
      </div>
    
    )
  }
}

export default Blog
