const Notification = ({ message }) => {
  
  const errorStyle = {
	color: red,
	background: lightgrey,
	fontSize: 20,
	borderStyle: solid,
	borderColor: green,
	borderRadius: 5,
	padding: 10,
	marginBottom: 10,
}

  const passStyle = {
	color: green,
	background: lightgrey,
	fontSize: 20,
	borderStyle: solid,
	borderColor: green,
	borderRadius: 5,
	padding: 10,
	marginBottom: 10,
}
  
  
  if (message === null) {
    return null
  }

  return (
    <div className='pass'>
      {message}
    </div>
  )
}

export default Notification;
