import {useState} from 'react'

const Blog = ({blog}) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  
  const [shown, setShown] = useState(false)
  
  const toggleDetail = () => {
    setShown(!(shown))
  }

  const label = shown
    ? 'hide'
    : 'view'
  
  if (shown && blog.user.personName !== 'undefined') {
    return (
      <div style={blogStyle}>
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
        <p>{blog.url}</p>
        <p>likes: {blog.likes} <button>like</button></p>
        <p>{blog.user.personName}</p>
      </div>
      </div>
    )
  } else {
    return (
      <div style={blogStyle}>
      <div>
        <p>"{blog.title}" by {blog.author} <button onClick={toggleDetail}>{label}</button> </p>
      </div>
      </div>
      
      )
  }  
}

export default Blog
