const Notification = ({ message }) => {

  if (!message) {
    return null
  }

  if (message.toLowerCase().includes('error')) {
    return(
      <div className='error'>
        {message}
      </div>
    )
  } else {
    return (
      <div className='pass'>
        {message}
      </div>
    )
  }
}

export default Notification
