import ListGroup from 'react-bootstrap/ListGroup'
//import Button from 'react-bootstrap/Button'

const BlogList = () => {

  const blogStyle = {
    paddingTop: 10,
    //paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5,
    backgroundColor: '#475876'
  }

  //const searchButtStyle = {
