import { useState } from 'react'
import loginService from '../services/login'
import { useField } from '../hooks/index'
import { useNavigate } from 'react-router-dom'
import Button from 'react-bootstrap/Button'

const newButtStyle = {
  backgroundColor: '#DEDEDB',
  borderColor: 'black',
  borderRadius: 25,
  borderWidth: 3,
  color: 'black',
  fontFamily: 'Tillana'
}

const hrStyle = {
  //padding: '10',
  color: '#94B9F0',
  borderWidth: 2
}

const textStyle = {
  fontFamily: 'Tillana',
  textAlign: 'center'
}

const LoginPage = () => {

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const handleLogin = async (event) => {
    event.preventDefault()

    try {
      const usering = await loginService.login({ username, password })
      window.localStorage.setItem( 'loggedBlogAppUser', JSON.stringify(usering) )
      //console.log(usering, 'is usering in handlelogin') <- this has value
      dispatch(userData(usering))
      dispatch(userToken(usering))
      setUsername('')
      setPassword('')
      dispatch(setNotif({ msg: `Welcome ${usering.personName}!`, variant: 'success' }, 5))
    } catch (exception) {
      dispatch(setNotif({ msg: 'Error: Wrong username or password', variant: 'error' }, 5))
      //console.log(exception, 'is exception')
    }
    navigate('/blogs')
  }


  return (
    <div style={textStyle}>
      <h2>Log in to the application</h2>
      <hr style={hrStyle}></hr>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            id='username'
            value={username}
            name="Username"
            onChange={handleUsernameChange}
            placeholder='Enter username'
          />
        </div>
        <hr style={hrStyle}></hr>
        <div>
          <input
            id='password'
            value={password}
            name="Password"
            onChange={handlePasswordChange}
            placeholder='Enter password'
          />
        </div>
        <hr style={hrStyle}></hr>
        <Button id="login-button" type="submit" style={newButtStyle}>login</Button> <Button style={newButtStyle}>forgot password?</Button>
      </form>
    </div>
  )
}

export default LoginPage
